import React from 'react';
import './ActivityList.css';

function ActivityList({ activities, onUpdateStreak, onToggleFreezeActivity, onDeleteStreak, onViewActivity, onArchiveActivity, onUnarchiveActivity }) {
    return (
        <ul>
            {activities.map((activity, index) => (
                <li
                    key={index}
                    className={`${activity.lastUpdated === new Date().toDateString() ? 'updated-today' : ''} ${activity.isArchived ? 'archived' : ''}`}
                    onClick={() => onUpdateStreak(index)}
                >
                    <span>{activity.name} - Streak: {activity.streak}</span>
                    {!activity.isArchived && (
                        <button
                            onClick={(e) => {
                                e.stopPropagation();
                                onToggleFreezeActivity(index);
                            }}
                        >
                            {activity.isFrozen ? 'Unfreeze' : 'Freeze'}
                        </button>
                    )}
                    {activity.isArchived ? (
                        <button
                            onClick={(e) => {
                                e.stopPropagation();
                                onUnarchiveActivity(index);
                            }}
                        >
                            Unarchive
                        </button>
                    ) : (
                        <button
                            onClick={(e) => {
                                e.stopPropagation();
                                onArchiveActivity(index);
                            }}
                        >
                            Archive
                        </button>
                    )}
                    <button
                        onClick={(e) => {
                            e.stopPropagation();
                            onViewActivity(index);
                        }}
                    >
                        View
                    </button>
                    <button
                        onClick={(e) => {
                            e.stopPropagation();
                            onDeleteStreak(index);
                        }}
                    >
                        Delete
                    </button>
                </li>
            ))}
        </ul>
    );
}

export default ActivityList;
