import React from 'react';

function ActivityList({ activities, onUpdateStreak, onDeleteStreak, onViewActivity }) {
    return (
        <ul>
            {activities.map((activity, index) => (
                <li key={index} style={{ backgroundColor: activity.isFrozen ? 'lightblue' : 'white' }}>
                    <span>{activity.name} - Streak: {activity.streak}</span>
                    <button onClick={() => onUpdateStreak(index)} disabled={activity.isFrozen}>Complete</button>
                    <button onClick={() => onViewActivity(index)}>View</button>
                    <button onClick={() => activity.isFrozen ? onUnfreezeActivity(index) : onFreezeActivity(index)}>
                        {activity.isFrozen ? 'Unfreeze' : 'Freeze'}
                    </button>
                    <button onClick={() => onDeleteStreak(index)}>Delete</button>
                </li>
            ))}
        </ul>
    );
}

export default ActivityList;
