import React, { useState } from 'react';
import './app.css'; // Ensure your CSS is imported
import ActivityList from './components/ActivityList';
import ActivityDetails from './components/ActivityDetails';

const App = () => {
    const [activities, setActivities] = useState([]);
    const [selectedActivity, setSelectedActivity] = useState(null);

    // Function to handle adding a new activity
    const handleAddActivity = (e) => {
        e.preventDefault();
        const activityName = e.target.elements['activity-name'].value;
        const activityStreak = parseInt(e.target.elements['activity-streak'].value) || 0;
        if (activityName) {
            setActivities([
                ...activities,
                {
                    name: activityName,
                    streak: activityStreak,
                    lastUpdated: new Date().toDateString(),
                    startDate: new Date().toDateString(),
                    contributions: [],
                },
            ]);
            e.target.reset(); // Clear form inputs after adding
        }
    };

    // Function to view activity details
    const handleViewActivity = (index) => {
        setSelectedActivity(activities[index]);
    };

    // Function to go back to the activity list
    const handleBack = () => {
        setSelectedActivity(null);
    };

    // Function to handle completing all activities
    const handleCompleteAll = () => {
        // Implement complete all functionality if needed
    };

    return (
        <div className="container">
            {selectedActivity ? (
                <ActivityDetails
                    activity={selectedActivity}
                    onBack={handleBack}
                />
            ) : (
                <>
                    <h1>Streak Tracker</h1>
                    <form onSubmit={handleAddActivity}>
                        <input
                            type="text"
                            name="activity-name"
                            placeholder="Enter activity name"
                        />
                        <input
                            type="number"
                            name="activity-streak"
                            placeholder="Enter starting streak"
                        />
                        <button type="submit">Add Activity</button>
                    </form>
                    <button onClick={handleCompleteAll}>Complete All</button>
                    <ActivityList
                        activities={activities}
                        onViewActivity={handleViewActivity}
                    />
                </>
            )}
        </div>
    );
};

export default App;
