import React, { useState, useEffect } from 'react';
import ActivityForm from './components/ActivityForm';
import ActivityList from './components/ActivityList';
import ActivityDetails from './components/ActivityDetails';
import './App.css'; // Import the CSS file here

function App() {
    const [activities, setActivities] = useState([]);
    const [selectedActivity, setSelectedActivity] = useState(null);

    useEffect(() => {
        const savedActivities = JSON.parse(localStorage.getItem('activities')) || [];
        setActivities(savedActivities);
    }, []);

    useEffect(() => {
        localStorage.setItem('activities', JSON.stringify(activities));
    }, [activities]);

    const addActivity = (name, streak) => {
        const today = new Date();
        const contributions = [];
        const needsContribution = !(name === "Morning/Evening Journal" || name === "Fitness/Exercise" || name === "StreakTracker" || name === "Personal Website");

        for (let i = 0; i < streak; i++) {
            const date = new Date();
            date.setDate(today.getDate() - i);
            const dateString = date.toDateString();
            const contribution = needsContribution ? prompt(`Enter streak contribution for ${dateString}`) : '';
            if (contribution || !needsContribution) {
                contributions.push({ date: dateString, contribution });
            }
        }

        const startDate = new Date();
        startDate.setDate(today.getDate() - (streak - 1));
        const newActivity = {
            name,
            streak,
            lastUpdated: today.toDateString(),
            startDate: startDate.toDateString(),
            contributions
        };

        setActivities([...activities, newActivity]);
    };

    const updateStreak = (index) => {
        const today = new Date().toDateString();
        const needsContribution = !(activities[index].name === "Morning/Evening Journal" || activities[index].name === "Fitness/Exercise" || activities[index].name === "StreakTracker" || activities[index].name === "Personal Website");

        if (needsContribution) {
            if (activities[index].lastUpdated !== today) {
                const contribution = prompt("Enter streak contribution for today:");
                if (contribution) {
                    const updatedActivities = activities.map((activity, i) => {
                        if (i === index) {
                            return {
                                ...activity,
                                streak: activity.streak + 1,
                                lastUpdated: today,
                                contributions: [...activity.contributions, { date: today, contribution }]
                            };
                        }
                        return activity;
                    });
                    setActivities(updatedActivities);
                }
            } else {
                alert("Streak already updated today.");
            }
        } else {
            const updatedActivities = activities.map((activity, i) => {
                if (i === index) {
                    return {
                        ...activity,
                        streak: activity.streak + 1,
                        lastUpdated: today
                    };
                }
                return activity;
            });
            setActivities(updatedActivities);
        }
    };

    const deleteStreak = (index) => {
        const updatedActivities = activities.filter((_, i) => i !== index);
        setActivities(updatedActivities);
    };

    const viewActivity = (index) => {
        setSelectedActivity(activities[index]);
    };

    const goBack = () => {
        setSelectedActivity(null);
    };

    return (
        <div className="container">
            {selectedActivity ? (
                <ActivityDetails activity={selectedActivity} onBack={goBack} />
            ) : (
                <>
                    <h1>Streak Tracker</h1>
                    <ActivityForm onAddActivity={addActivity} />
                    <ActivityList
                        activities={activities}
                        onUpdateStreak={updateStreak}
                        onDeleteStreak={deleteStreak}
                        onViewActivity={viewActivity}
                    />
                </>
            )}
        </div>
    );
}

export default App;
