import React, { useState, useEffect } from 'react';
import ActivityList from './components/ActivityList';
import ActivityDetails from './components/ActivityDetails';
import './App.css';

function App() {
    const [activities, setActivities] = useState(() => {
        const storedActivities = JSON.parse(localStorage.getItem('activities'));
        return storedActivities || [];
    });
    const [selectedActivity, setSelectedActivity] = useState(null);

    // Save activities to localStorage whenever they are updated
    useEffect(() => {
        console.log("Saving activities to localStorage:", activities);
        localStorage.setItem('activities', JSON.stringify(activities));
    }, [activities]);

    const handleUpdateStreak = (index) => {
        const today = new Date().toDateString();
        const needsContribution = !(activities[index].name === "Fitness/Exercise");

        const newActivities = [...activities];

        if (needsContribution) {
            if (newActivities[index].lastUpdated !== today) {
                const contribution = prompt("Enter streak contribution for today:");
                if (contribution) {
                    newActivities[index].streak++;
                    newActivities[index].lastUpdated = today;
                    newActivities[index].contributions.push({ date: today, contribution });
                    setActivities(newActivities);
                }
            } else {
                alert("Streak already updated today.");
            }
        } else {
            newActivities[index].streak++;
            newActivities[index].lastUpdated = today;
            setActivities(newActivities);
        }
    };

    const handleToggleFreezeActivity = (index) => {
        const newActivities = [...activities];
        newActivities[index].isFrozen = !newActivities[index].isFrozen;
        setActivities(newActivities);
    };

    const handleDeleteStreak = (index) => {
        console.log(`Delete activity at index ${index}`);
        setActivities(activities.filter((_, i) => i !== index));
    };

    const handleViewActivity = (index) => {
        setSelectedActivity(activities[index]);
    };

    const handleCompleteAllActivities = () => {
        console.log('Complete all activities');
        // Functionality will be added later
    };

    const handleAddActivity = (e) => {
        e.preventDefault();
        const activityName = e.target.elements['activity-name'].value;
        const activityStreak = parseInt(e.target.elements['activity-streak'].value) || 0;
        if (activityName) {
            setActivities([
                ...activities,
                {
                    name: activityName,
                    streak: activityStreak,
                    lastUpdated: new Date().toDateString(),
                    startDate: new Date().toDateString(),
                    contributions: [],
                    isFrozen: false, // Initialize the isFrozen property
                },
            ]);
            e.target.reset(); // Clear form inputs after adding
        }
    };

    const goBack = () => {
        setSelectedActivity(null);
    };

    return (
        <div className="container">
            {selectedActivity ? (
                <ActivityDetails activity={selectedActivity} goBack={goBack} />
            ) : (
                <>
                    <form id="add-activity-form" onSubmit={handleAddActivity}>
                        <input type="text" id="activity-name" name="activity-name" placeholder="Enter activity name" />
                        <input type="number" id="activity-streak" name="activity-streak" placeholder="Enter starting streak" />
                        <button type="submit">Add Activity</button>
                    </form>
                    <button onClick={handleCompleteAllActivities}>Complete All</button>
                    <ActivityList
                        activities={activities}
                        onUpdateStreak={handleUpdateStreak}
                        onToggleFreezeActivity={handleToggleFreezeActivity}
                        onDeleteStreak={handleDeleteStreak}
                        onViewActivity={handleViewActivity}
                    />
                </>
            )}
        </div>
    );
}

export default App;
