import React, { useState } from 'react';
import ActivityList from './components/ActivityList';
import './App.css';

function App() {
    const [activities, setActivities] = useState([]);

    // Placeholder functions for activity management
    const handleUpdateStreak = (index) => {
        console.log(`Update streak for activity at index ${index}`);
        // Functionality will be added later
    };

    const handleUnfreezeActivity = (index) => {
        console.log(`Unfreeze activity at index ${index}`);
        // Functionality will be added later
    };

    const handleFreezeActivity = (index) => {
        console.log(`Freeze activity at index ${index}`);
        // Functionality will be added later
    };

    const handleDeleteStreak = (index) => {
        console.log(`Delete activity at index ${index}`);
        // Functionality will be added later
    };

    const handleViewActivity = (index) => {
        console.log(`View details for activity at index ${index}`);
        // Functionality will be added later
    };

    const handleCompleteAllActivities = () => {
        console.log('Complete all activities');
        // Functionality will be added later
    };

    return (
        <div className="container">
            <form
                id="add-activity-form"
                onSubmit={(e) => {
                    e.preventDefault();
                    // Logic to add an activity (not implemented here)
                }}
            >
                <input type="text" id="activity-name" placeholder="Enter activity name" />
                <input type="number" id="activity-streak" placeholder="Enter starting streak" />
                <button type="submit">Add Activity</button>
            </form>
            <button onClick={handleCompleteAllActivities}>Complete All</button>
            <ActivityList
                activities={activities}
                onUpdateStreak={handleUpdateStreak}
                onUnfreezeActivity={handleUnfreezeActivity}
                onFreezeActivity={handleFreezeActivity}
                onDeleteStreak={handleDeleteStreak}
                onViewActivity={handleViewActivity}
            />
        </div>
    );
}

export default App;
