import React from 'react';
import './ActivityList.css';

function ActivityList({ activities, onUpdateStreak, onToggleFreezeActivity, onDeleteStreak, onViewActivity }) {
    return (
        <ul>
            {activities.map((activity, index) => (
                <li
                    key={index}
                    className={activity.lastUpdated === new Date().toDateString() ? 'updated-today' : ''}
                    onClick={() => onUpdateStreak(index)}
                >
                    <span>{activity.name} - Streak: {activity.streak}</span>
                    <button
                        onClick={(e) => {
                            e.stopPropagation();
                            onToggleFreezeActivity(index);
                        }}
                    >
                        {activity.isFrozen ? 'Unfreeze' : 'Freeze'}
                    </button>
                    <button
                        onClick={(e) => {
                            e.stopPropagation();
                            onViewActivity(index);
                        }}
                    >
                        View
                    </button>
                    <button
                        onClick={(e) => {
                            e.stopPropagation();
                            onDeleteStreak(index);
                        }}
                    >
                        Delete
                    </button>
                </li>
            ))}
        </ul>
    );
}

export default ActivityList;
