// src/components/ActivityDetails.js
import React from 'react';

const ActivityDetails = ({ activity, goBack }) => {
    const activityStartDate = new Date(activity.startDate);

    return (
        <div className="details-view">
            <h1>{activity.name}</h1>
            <p><strong>Streak:</strong> {activity.streak}</p>
            <p><strong>Date Started:</strong> {activityStartDate.toDateString()}</p>
            <p><strong>Last Updated:</strong> {activity.lastUpdated || 'Never'}</p>
            {activity.contributions && activity.contributions.length > 0 && (
                <>
                    <strong>Log:</strong>
                    <ul>
                        {activity.contributions.map((contribution, index) => (
                            <li key={index}>
                                <strong>{contribution.date}:</strong> <span>{contribution.contribution}</span>
                            </li>
                        ))}
                    </ul>
                </>
            )}
            <button onClick={goBack}>Back</button>
        </div>
    );
};

export default ActivityDetails;
