import React from 'react';

const ActivityList = ({ activities, onUpdateStreak, onToggleFreezeActivity, onDeleteStreak, onViewActivity }) => {
    return (
        <div className="activity-list">
            {activities.map((activity, index) => (
                <div key={index} className="activity-item">
                    <h2>{activity.name}</h2>
                    <p>Streak: {activity.streak}</p>
                    <button onClick={() => onUpdateStreak(index)}>Update Streak</button>
                    <button onClick={() => onToggleFreezeActivity(index)}>
                        {activity.isFrozen ? 'Unfreeze' : 'Freeze'}
                    </button>
                    <button onClick={() => onDeleteStreak(index)}>Delete</button>
                    <button onClick={() => onViewActivity(index)}>View</button>
                </div>
            ))}
        </div>
    );
};

export default ActivityList;
