// src/components/ActivityDetails.js
import React from 'react';

const ActivityDetails = ({ activity, onBack }) => {
    const { name, streak, startDate, lastUpdated, contributions } = activity;

    return (
        <div className="activity-details">
            <h2>{name}</h2>
            <p><strong>Streak:</strong> {streak}</p>
            <p><strong>Date Started:</strong> {new Date(startDate).toDateString()}</p>
            <p><strong>Last Updated:</strong> {lastUpdated || 'Never'}</p>
            {contributions.length > 0 && (
                <>
                    <strong>Log:</strong>
                    <ul>
                        {contributions.map((contribution, index) => (
                            <li key={index}>
                                <strong>{contribution.date}:</strong> {contribution.contribution}
                            </li>
                        ))}
                    </ul>
                </>
            )}
            <button onClick={onBack}>Back</button>
        </div>
    );
};

export default ActivityDetails;
